import React, { Component} from 'react'
import '../Product/Product.css'

class CardProduct extends Component{
    state = {
        order : 4
    }

    handleCounterChange = () => {
        this.props.onCounterChange(5)
    }
    
    handlePlus = () => {
        this.setState({
            order : this.state.order + 1 
        })

        this.handleCounterChange();
    }

    handleMinus = () => {
        if(this.order >0 ){
            this.setState({
            order : this.state.order - 1 
        })}
    }

    render(){
        return(
            <div className="card">
                <div className="img-thumb-prod">
                    <img src="https://www.konveksipadang.com/wp-content/uploads/2018/08/GROSIR-KAOS-POLOS-DI-PADANG-MURAH-DENGAN-KUALITAS-TERJAMIN.jpg" alt="product_image"/>
                </div>
                <p className="product-title">Kaos Polos Pastel Color</p>
                <p className="product-price">Rp. 34.000</p>                    <div className="counter">
                    <button className="minus" onClick = {this.handleMinus}>-</button>
                    <input type="text" value={this.state.order}/>
                      <button className="minus" onClick = {this.handlePlus}>+</button>
                </div>
            </div>
        );
    }
}

export default CardProduct;
