import React, { Component, Fragment } from 'react'
import CardProduct from '../CardProduct/CardProduct'
import './Product.css'

class Product extends Component{
    state = {
        order : 4
    }
    handlePlus = () => {
        this.setState({
            order: this.state.order + 1
        })
    }

    handleMinus = () => {
        if(this.state.order >0){
            this.setState({
                order: this.state.order - 1
            })
        }     
    }
    render(){
        return(
            <Fragment>
                <div className="header">
                    <div className="logo">
                        <img src="https://i.pinimg.com/originals/98/58/9e/98589efd855f1aba68d9a45413c289ba.png" alt="product_logo"/>
                    </div>
                    <div className="trolley">
                        <img src="https://w7.pngwing.com/pngs/819/790/png-transparent-suitcase-baggage-computer-icons-trolley-bus-suitcase-rectangle-suitcase-black.png" alt="trolley"/>
                        <div className="count">{this.state.order}</div>
                    </div>
                </div>
                <CardProduct on/>
            </Fragment>
        );
    }
}

export default Product;
