const initState = {
    totalOrder : 0
  }
  
  // reducer
  const rootReducer = (state =  initState ,action) =>{
    if (action.type === 'PLUS_ORDER') {
      return{
        ...state,
        totalOrder : state.totalOrder +1
      }
    }
    if (action.type === 'MINUS_ORDER') {
      let totalOrder = 0
      if (state.totalOrder >0) {
        totalOrder = state.totalOrder - 1
      }
      return{
        ...state,
        totalOrder : totalOrder
      }
    }
    return state;
  }

  export default rootReducer();
