import React, { Component, Fragment } from 'react'
import './BlogPost.css'
import Post from '../../../component/Post/Post'
import axios from 'axios'
import API from '../../../services'

class BlogPost extends Component{
    state = {
        post: [],
        formBlogPost: {
            id    : 1,
            title : '',
            body  : '',
            userId: 1
        },
        isUpdate: false,
        comments: []
    }
    
    componentDidMount(){
        this.getPostAPI()
    }
    
    getPostAPI = () => {
        API.getNewsBlog().then(result => {
            this.setState({
                post: result
            })
        })
    }
    
    putDataAPI = () => {
        axios.put(`http://localhost:3004/posts/${this.state.formBlogPost.id}`, this.state.formBlogPost)
        .then((res) => {
            console.log(res)
            this.getPostAPI()
            this.setState({
                isUpdate: false,
                formBlogPost: {
                    id    : 1,
                    title : '',
                    body  : '',
                    userId: 1
                }
            })
        })
    }
        
    postDataAPI = () =>{
        axios.post('http://localhost:3004/posts', this.state.formBlogPost)
        .then((res) =>{
            console.log(res);
            this.getPostAPI();
            this.setState({
                isUpdate: false,
                formBlogPost: {
                    id    : 1,
                    title : '',
                    body  : '',
                    userId: 1
                }
            })
        }, (err) => {
            console.log('error', err);
        })
    }
    
    handleUpdate = (data) => {
            console.log(data);
        this.setState({
            isUpdate    : true,
            formBlogPost: data
        })
    }

    handleRemove = (data) => {
        axios.delete(`http://localhost:3004/posts/${data}`)
        .then(() => {
            this.getPostAPI()
        })
    }

    handleFormChange = (event) => {
        let formBlogPostNew = {...this.state.formBlogPost};
        let timestamp       = new Date().getTime();
        if(!this.state.isUpdate){
            formBlogPostNew['id'] = timestamp;
        }
        formBlogPostNew[event.target.name] = event.target.value;
        this.setState({
            formBlogPost : formBlogPostNew
        }, () => {
            console.log('value form blogpost', this.state.formBlogPost)
        })
    }
    
    handleSubmit = () => {
        if (this.state.isUpdate){
            this.putDataAPI()
        }else{
            this.postDataAPI()
        }
    }



    handleDetail = (id) => {
        this.props.history.push(`/detail-post/${id}`)
    }

    render(){
        return(
            <Fragment>
                <p     className = 'selection-title'>Blog Post</p>
                <div   className = "form-add-post">
                <label htmlFor   = "title">title</label>
                <input value     = {this.state.formBlogPost.title} type = "text" name = "title" placeholder = "add title"  onChange = {this.handleFormChange}/>
                <label htmlFor   = "body">body content</label>
                    <textarea 
                            value       = {this.state.formBlogPost.body}
                            name        = "body" id            = "body" cols = "30" rows = "10"
                            placeholder = "add body content"
                            onChange    = {this.handleFormChange}></textarea>
                    <button className   = "btn-submit" onClick = {this.handleSubmit} >Simpan</button>
                </div>
                {
                    this.state.comments.map(comm)
                }
                {
                  this.state.post.map(post => {
                        return <Post 
                        key      = {post.id}
                        data     = {post}
                        remove   = {this.handleRemove}
                        update   = {this.handleUpdate}
                        goDetail = {this.handleDetail}/>
                    })
                }
            </Fragment>
        )
    }
}

export default BlogPost;
