import React, { Component, Fragment } from 'react'
import './Product.css'

class Product extends Component{
    state = {
        order : 4
    }
    handlePlus = () => {
        alert ('Plus work');
    }

    handleMinus = () => {
        al
    }
    render(){
        return(
            <Fragment>
                <div className="header">
                    <div className="logo">
                        <img src="https://i.pinimg.com/originals/98/58/9e/98589efd855f1aba68d9a45413c289ba.png" alt="product_logo"/>
                    </div>
                    <div className="trolley">
                        <img src="https://w7.pngwing.com/pngs/819/790/png-transparent-suitcase-baggage-computer-icons-trolley-bus-suitcase-rectangle-suitcase-black.png" alt="trolley"/>
                        <div className="count">{this.state.order}</div>
                    </div>
                </div>
                <div className="card">
                    <div className="img-thumb-prod">
                        <img src="https://www.konveksipadang.com/wp-content/uploads/2018/08/GROSIR-KAOS-POLOS-DI-PADANG-MURAH-DENGAN-KUALITAS-TERJAMIN.jpg" alt="product_image"/>
                    </div>
                    <p className="product-title">Kaos Polos Pastel Color</p>
                    <p className="product-price">Rp. 34.000</p>
                    <div className="counter">
                        <button className="minus" onClick = {this.handleMinus}>-</button>
                        <input type="text" value={this.state.order}/>
                        <button className="minus" onClick = {this.handlePlus}>+</button>
                    </div>
                </div>
            </Fragment>
        );
    }
}

export default Product;
