import React, { Component, Fragment } from 'react'
import './BlogPost.css'
import Post from '../../component/Post/Post'
import axios from 'axios'

class BlogPost extends Component{
    state = {
        post: [],
        formBlogPost: {
            id:1,
            title: '',
            body: '',
            userId: 1
        }
    }

    getPostAPI = () => {
        axios.get('http://localhost:3004/posts?_sort=id&_order=desc')
        .then((result) => {
            this.setState({
                post: result.data
            })
        })
    }
    handleRemove = (data) => {
        axios.delete(`http://localhost:3004/posts/${data}`)
        .then((result) => {
            this.getPostAPI()
        })
    }

    handleFormChange = (event) => {
        let formBlogPostNew = {...this.state.formBlogPost};
        let timestamp = new Date().getTime();
        formBlogPostNew['id'] = timestamp;
        formBlogPostNew [event.target.name] = event.target.value;
        this.setState({
            formBlogPost : formBlogPostNew
        }, () => {
            console.log('value form blogpost', this.state.formBlogPost)
        })
    }

    
    postDataAPI = () =>{
        axios.post('http://localhost:3004/posts', this.state.formBlogPost)
        .then((res) =>{
            console.log(res);
            this.getPostAPI();
        }, (err) => {
            console.log('error', err);
        })
    }
    
    handleSubmit = (event) => {
        let formBlogPostNew = {...this.state.formBlogPost};
        this.postDataAPI()
        .then(() => {
            event.preventDefault();
            this.setState({
                formBlogPost: {
                    id:1,
                    title: '',
                    body: '',
                    userId: 1
                }
            })
        })
    }

    componentDidMount(){
        this.getPostAPI()
    }

    render(){
        return(
            <Fragment>
                <p className = 'selection-title'>Blog Post</p>
                <div className="form-add-post">
                    <label htmlFor="title">title</label>
                    <input value = {this.state.title} type="text" name = "title" placeholder = "add title"  onChange = {this.handleFormChange}/>
                    <label htmlFor="body">body content</label>
                    <textarea value = {this.state.body} name="body" id="body" cols="30" rows="10" placeholder = "add body content" onChange = {this.handleFormChange}></textarea>
                    <button className="btn-submit" onClick = {this.handleSubmit} >Simpan</button>
                </div>
                {
                  this.state.post.map(post => {
                        return <Post key= {post.id} data = {post} remove = {this.handleRemove} />
                    })
                }
            </Fragment>
        )
    }
}

export default BlogPost;
