import React, { Component } from 'react'
import { connect } from 'react-redux'
import '../Product.css'

class Counter extends Component{
    // state = {
    //     order : 0
    // }

    handleCounterChange = (newValue) => {
        this.props.onCounterChange(newValue);
    }
    
    handlePlus = () => {
        this.setState({
            order : this.state.order + 1 
        }, () => {
            this.handleCounterChange(this.state.order);
        })
    }

    handleMinus = () => {
        if(this.state.order >0 ){
            this.setState({
                order : this.state.order - 1 
            }, () => {
                this.handleCounterChange(this.state.order);
            })
        }    
    }

    render() {
        console.log(this.props)
        return (
            <div    className = "counter">
                <button className = "minus" onClick = {this.handleMinus}>-</button>
                <input  type      = "text"  value   = {this.props.order}/>
                <button className = "minus" onClick = {this.handlePlus}>+</button>
            </div>
        );
    }
}
const mapStateToProps = (state) => {
    return {
        order : state.totalOrder
    }
}

const mapDispatchToProps = (dispatcg) => {
    return {
        handlePlus: () => dispatchEvent({type: 'PLUS_ORDER'}),
        handleMinus: () => dispatchEvent({type: 'MINUS_ORDER'})
    }
}


export default connect(mapStateToProps, ma)(Counter);
