const Hooks = () => {
    const [count, setCount] = useState(0);

    useEffect(() => {
        document.title  = `title change : ${this.state.count}`
    })

    return(
        <div className = "hooks">
            <p>Nilai saya saat ini adalah : {count}</p>
            <button onClick = {() => setCount(count + 1)}>Update nilai</button>
        </div>
    )
}

export default Hooks;
