import axios from 'axios'
import {onlineRoot, rootPath, primbonAPI} from './Config'

const Get = (path, root) =>{
    const promise = new Promise((resolve, reject) => {
        axios.get(`${root ? onlineRoot : rootPath}/${path}`)
        .then((result) => {
                resolve(result.data);
            }, (err) =>{
                reject(err);
        })
    })
    return promise
}

export default Get;
